INSERT INTO postcards (id, title, description, image, price, available, featured, created_at, updated_at) VALUES
('postcard_sunset_001', 'Авторская открытка "Закат"', 'Уникальная открытка с авторским рисунком заката над городом. Создана с использованием акварели и туши.', 'https://example.com/postcard-sunset.jpg', 2900, true, true, NOW(), NOW()),
('postcard_minimal_002', 'Открытка "Минимализм"', 'Стильная минималистичная открытка в черно-белых тонах. Идеально подходит для современных интерьеров.', 'https://example.com/postcard-minimal.jpg', 2900, true, false, NOW(), NOW()),
('postcard_flowers_003', 'Цветочная композиция', 'Нежная открытка с изображением полевых цветов. Выполнена цветными карандашами.', 'https://example.com/postcard-flowers.jpg', 3200, true, true, NOW(), NOW());

-- Добавление тестовых писем (нужно заменить 'user_id_here' на реальный ID пользователя-автора)
INSERT INTO letters (id, title, slug, content, published, author_id, created_at, updated_at) VALUES
('letter_welcome_001', 'Добро пожаловать в мир авторских открыток', 'dobro-pozhalovat-v-mir-avtorskih-otkrytok', '<h2>Дорогие читатели!</h2><p>Рад приветствовать вас в новом разделе нашего сайта — <strong>Letters</strong>. Здесь будут собираться все мои письма, размышления и анонсы новых проектов.</p><p>Сегодня хочу поделиться с вами особенным проектом — коллекцией авторских открыток. Каждая открытка создается вручную, с особым вниманием к деталям и атмосфере.</p><h3>Что делает эти открытки особенными?</h3><ul><li><strong>Уникальный дизайн</strong> — каждая открытка создается индивидуально</li><li><strong>Качественные материалы</strong> — используем только лучшую бумагу</li><li><strong>Персонализация</strong> — можете добавить свое сообщение</li><li><strong>Международная доставка</strong> — отправляем по всему миру</li></ul><p>В следующих письмах расскажу больше о процессе создания и поделюсь историями, которые вдохновляют на новые работы.</p><p>С теплотой,<br>Антон</p>', true, 'user_id_here', NOW(), NOW()),

('letter_process_002', 'Процесс создания: от идеи до открытки', 'process-sozdaniya-ot-idei-do-otkrytki', '<h2>За кулисами творчества</h2><p>Многие спрашивают, как рождаются идеи для открыток. Сегодня приоткрою завесу тайны творческого процесса.</p><h3>Этап 1: Вдохновение</h3><p>Все начинается с момента. Это может быть:</p><ul><li>Утренний свет, падающий на стену</li><li>Случайная встреча на улице</li><li>Строчка из книги</li><li>Мелодия, которая задела за живое</li></ul><h3>Этап 2: Скетч</h3><p>Идея всегда начинается с быстрого наброска. Иногда это просто пятно или линия, но в них уже живет будущая открытка.</p><h3>Этап 3: Воплощение</h3><p>Самый долгий и кропотливый этап. Здесь важен каждый штрих, каждый оттенок.</p><p>Каждая открытка — это маленькая история, которую я хочу рассказать миру.</p><p>До встречи в следующем письме!</p>', true, 'user_id_here', NOW(), NOW()),

('letter_personal_003', 'Искусство персонализации: делаем открытки особенными', 'iskusstvo-personalizatsii-delaem-otkrytki-osobennymi', '<h2>Личное прикосновение к каждой открытке</h2><p>Что превращает обычную открытку в особенную? Персонализация — вот секрет, который делает каждую работу уникальной.</p><h3>Индивидуальные сообщения</h3><p>Каждое сообщение, которое вы добавляете к открытке, становится частью ее истории. Это может быть:</p><ul><li>Поздравление с особенной датой</li><li>Признание в любви</li><li>Слова поддержки в трудный момент</li><li>Просто теплые пожелания</li></ul><h3>Выбор цветовой палитры</h3><p>В зависимости от повода и настроения, я подбираю цвета, которые лучше всего передадут ваши чувства.</p><p>Помните: открытка — это не просто картинка, это способ передать частичку души.</p><p>С уважением,<br>Антон</p>', true, 'user_id_here', NOW(), NOW());

-- Примечание: Замените 'user_id_here' на реальный ID пользователя из таблицы users
